userDefinedLinkHintCss:
/* Hacker Theme */

/* Link hint boxes */
div > .vimiumHintMarker {
  background: #000000;
  font-size: 12px;
  border: 0.15em solid #00FF15;
  border-radius: 1.0em;
  box-shadow: 0em 0.1em 0.6em 0.1em rgba(0, 255, 15, 0.2);
}

/* Link hint text */
div > .vimiumHintMarker span {
  color: #00FF15;
  font-size: inherit;
  text-shadow: none;
}

/* Link hint matching characters */
div > .vimiumHintMarker > .matchingCharacter {
  color: #00FF15;
}

input#vomnibarInput.vimiumReset {
	color: #00FF15 !important;
  background-color: #111 !important;
}

#vomnibar {
   border-color: black;
   background-color: black;
   box-shadow: 0em 0.1em 0.6em 0.1em rgba(0, 255, 15, 0.4);
}

#vomnibar .vomnibarSearchArea {
   background-color: #222;
   border-bottom: #3f3f3f;
}

#vomnibar li .vomnibarTopHalf .vomnibarSource {
	color: #00FF15;
}

#vomnibar input {
   border-color: #393939;
}

#vomnibar ul {
    background-color: #222;
}

#vomnibar li {
   border-bottom: #3f3f3f;
}

#vomnibar li .vomnibarUrl {
   color: #00FF00;
}

#vomnibar li .vomnibarMatch {
   color: white;
}

#vomnibar li.vomnibarSelected {
    background-color: rgba(48, 119, 15, 0.5);
}

#vomnibar li em .vomnibarMatch, #vomnibar li .vomnibarTitle .vomnibarMatch {
   color: white;
}

#vomnibar li em, #vomnibar li .vomnibarTitle {
   color: #ddd;
}

/* Link hint matching characters */
div > .vimiumHintMarker > .matchingCharacter {
  color: #FFFFFF;
}
